import React, { useState, useEffect } from "react";


const App = () =>{
    const [num,setNum] = useState(0);
    const Clicked = ()=>{
        setNum(num+1);
    }

    useEffect( ()=>{
        document.title(`You clicked me ${num} times`)
    })


return (
    <>
    <button className="btn" onClick={Clicked}>CLICK ME {num}</button>
    </>
);
}


export default App;
