import React, { useState, useEffect } from "react";
import axios from "axios";


const App = () =>{
    const [num,setNum] = useState("INDIA");
    const [name,setName] = useState();
    const [confirm,setConfirm] = useState();
    const [recovered,setRecovered] = useState();
    const [death,setDeath] = useState();
    const [ReportDate,setReportDate] = useState();
    const [recentConfirm,setrecentConfirm] = useState();
    const [recentRecovered,setrecentRecovered] = useState();
    const [todaysdeath,setTodaysdeath] = useState();
    const FUNC1 = (event) =>{
        setNum(event.target.value)
    }

    useEffect( () =>{
        async function getdata(){
            const res = await axios.get("https://api.covid19api.com/summary");
        
        setName(res.data.Countries[76].Country);
        setConfirm(res.data.Countries[76].TotalConfirmed);
        setRecovered(res.data.Countries[76].TotalRecovered);
        setrecentConfirm(res.data.Countries[76].NewConfirmed);
        setrecentRecovered(res.data.Countries[76].NewConfirmed);
        setDeath(res.data.Countries[76].TotalDeaths);
        setReportDate(res.data.Countries[76].Date)
        setTodaysdeath(res.data.Countries[76].NewDeaths);
        }
        

        getdata();
    })
    

    return (
        <div className="main_div">
            <br/>
            <h1>You Have Selected <span id="green">{num}</span></h1>
            <br/>
            <h1>Country Name : <span>{name}</span></h1>
            <br/>
            <h1>DATE : <span>{ReportDate}</span></h1>
            <br/>
            <h1>Today's Covid-19 Cases : <span>{recentConfirm}</span></h1>
            <br/>
            <h1>Total Covid-19 Cases : <span>{confirm}</span></h1>
            <br/>
            <h1>Today's Recovery : <span>{recentRecovered}</span></h1>
            <br/>
            <h1>Total Recovered : <span>{recovered}</span></h1>
            <br/>
            <h1>Today's deaths : <span>{todaysdeath}</span></h1>
            <br/>
            <h1>Total deaths : <span>{death}</span></h1>
            <br/>
            <select value={num} onChange={FUNC1}>
                <option value="INDIA"> India</option>
                <option value="Canada">Canada</option>
                <option value="Egypt"> Egypt</option>
                <option value="Luxembourg"> Luxembourg</option>
                <option value=""> </option>
            </select>
        </div>
    )
}

export default App;
