import React, { useState, useEffect } from "react";
import axios from "axios";


const App = () =>{
    const [num,setNum] = useState();
    const [name,setName] = useState();
    const [confirm,setConfirm] = useState();
    const [recovered,setRecovered] = useState();
    const [death,setDeath] = useState();
    const [ReportDate,setReportDate] = useState();


    const FUNC1 = (event) =>{
        setNum(event.target.value)
    }

    useEffect( () =>{
        async function getdata(){
            const res = await axios.get("https://api.covid19api.com/summary");
        
        // console.log(res.data.Countries[76]);
        setName(res.data.Countries[76].Country);
        setConfirm(res.data.Countries[76].NewConfirmed);
        setRecovered(res.data.Countries[76].NewRecovered);
        setDeath(res.data.Countries[76].TotalDeaths);
        setReportDate(res.data.Countries[76].Date)
        }
        

        getdata();
    })
    

    return (
        <div className="main_div">
            <br/>
            <h1>You Have Selected {num}</h1>
            <br/>
            <h1>Name : <span>{name}</span></h1>
            <br/>
            <h1>DATE : <span>{ReportDate}</span></h1>
            <br/>
            <h1>Total Covid19 Cases <span>{confirm}</span></h1>
            <br/>
            <h1>Total Recovered <span>{recovered}</span></h1>
            <br/>
            <h1>Total deaths <span>{death}</span></h1>
            <br/>
            <select value={num} onChange={FUNC1}>
                <option value="1"> India</option>
                <option value="2"> 2</option>
                <option value="3"> 3</option>
                <option value="4"> 4</option>
                <option value="5"> 5</option>
            </select>
        </div>
    )
}

export default App;
