import React ,{ useState , useEffect } from "react";
import "./index.css";

const App = () =>{
    const [num,setNum] = useState(0);
    const [nums,setNums] = useState(0);


    useEffect(
        ()=>{
            alert("I am Clicked!!")
        },[num2]
    )

    const Clicked = () => {
        setNum(num+1)
    }  
    const Clicked2 = () => {
        setNums(nums+5)
    }

    return (
        <>
        <button  className="bala" onClick={Clicked}>
            click me {num}
        </button>

        <br/>
        <br/>
        <br/>
        <br/>
        
        <button  className="bala" onClick={Clicked2}>
            click me {nums}
        </button>

        </>
    )
}

export default App;
